          e.preventDefault();
          setHighlighted(false);

          Array.from(e.dataTransfer.files)
            .filter((file) => file.type === "text/csv")
            .forEach(async (file) => {
              const body = {
                url: 'http://alive5.com/xyz123?firstname=dustin&email=dustin@alive5.com'
