                method: 'POST',
                redirect: 'follow',
                // body: JSON.stringify(body),
                headers: {
                  'Content-Type': 'image/png',
                  'Authorization': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..-tc-ZjbTw20mKaXJaSl6tOw8w5hkm584Q-TsFInCUbo'
                }
              }
              const api = await fetch('https://api.alive5.com/1.0/qr/create', request)
